module.exports = (context) => {
  return {
    'MemberExpression': (node) => { // eslint-disable-line
      if (node.property !== undefined) {
        if (node.property.name === 'bind' ||
          node.property.value === 'bind'
        ) {
          context.report(node, 'Please dont use bind, as it could create problems in old browser.', {});
        }
      }
    }
  };
};
