module.exports = (context) => {
  return {
    'MemberExpression': (node) => { // eslint-disable-line
      if (!node.property) return;
      // should accept api.collections.forEach
      if (node.object.type !== 'MemberExpression' && node.object.name !== 'collections') {
        if ((node.property.name === 'forEach' ||
          node.property.value === 'forEach')
        ) {
          context.report(node, 'forEach is not cross browser compatible.', {});
        }
      }
    }
  };
};
