module.exports = (context) => {
  return {
    'MemberExpression': (node) => { // eslint-disable-line
      if (!node.property) return;
      if (node.object.type !== 'MemberExpression' && node.object.name !== 'collections') {
        if (node.property.name === 'map' ||
          node.property.value === 'map'
        ) {
          context.report(node, 'Please dont use map, as it could create problems in old browser.', {});
        }
      }
    }
  };
};
