const rule = require('../../lib/no-bind');
const RuleTester = require('eslint').RuleTester;

const ruleTester = new RuleTester();
ruleTester.run('no-bind', rule, {
  valid: [],
  invalid: [{
    code: 'var t = function () {console.log(this);} \n var p = t.bind({k: "m"})',
    errors: [{
      message: 'Please dont use bind, as it could create problems in old browser.',
      type: 'MemberExpression'
    }]
  }]
});
