const rule = require('../../lib/no-extendclass');
const RuleTester = require('eslint').RuleTester;

const ruleTester = new RuleTester();
ruleTester.run('no-extendclass', rule, {
  valid: [],
  invalid: [{
    code: 'class nullExtends extends null { constructor() {} }',
    parserOptions: { ecmaVersion: 6, sourceType: 'module' },
    errors: [{
      message: 'Class extend is not supported.',
      type: 'ClassDeclaration'
    }]
  }]
});
